import React from "react";
import { useLocation, useNavigate } from "react-router-dom";
import Navbar from "./Navbar";

const Home = () => {
  const navigate = useNavigate();
  const location = useLocation();
  const homeData = location.state;
  const isLoggedIn = false;
  if (homeData != null) {
    isLoggedIn = true;
  }
  const handleCreateShopClick = () => {
    // Navigate to a different page, e.g., a shop creation page
    navigate("/create-shop");
  };

  return (
    <div>
      <Navbar isLoggedIn={isLoggedIn} />
      <div style={styles.container}>
        <h1 style={styles.heading}>Your own online shop just one click away</h1>
        <button style={styles.button} onClick={handleCreateShopClick}>
          Create a shop
        </button>
        <h1 style={styles.heading}>And make your dream comes true.</h1>
      </div>
    </div>
  );
};

const styles = {
  container: {
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
    justifyContent: "center",
    height: "80vh",
    textAlign: "center",
  },
  heading: {
    fontSize: "2.5rem",
    marginBottom: "20px",
  },
  button: {
    fontSize: "1.25rem",
    padding: "15px 30px",
    backgroundColor: "green",
    color: "white",
    border: "none",
    borderRadius: "5px",
    cursor: "pointer",
  },
};

export default Home;
