import React from "react";
import { useNavigate } from "react-router-dom";

const Home = () => {
  const navigate = useNavigate();

  const handleCreateShopClick = () => {
    // Navigate to a different page, e.g., a shop creation page
    navigate("/create-shop");
  };

  return (
    <div style={styles.container}>
      <h1 style={styles.heading}>Your own online shop just one click away</h1>
      <button style={styles.button} onClick={handleCreateShopClick}>
        Create a shop
      </button>
      <h1 style={styles.heading}>And make your dream comes true.</h1>
    </div>
  );
};

const styles = {
  container: {
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
    justifyContent: "center",
    height: "50vh",
    textAlign: "center",
  },
  heading: {
    fontSize: "2.5rem",
    marginBottom: "20px",
  },
  button: {
    fontSize: "1.25rem",
    padding: "15px 30px",
    backgroundColor: "green",
    color: "white",
    border: "none",
    borderRadius: "5px",
    cursor: "pointer",
  },
};

export default Home;
