import React from "react";
import { useNavigate } from "react-router-dom";
import { FaShopify } from "react-icons/fa"; // Import an icon for visual appeal

const Billboard = () => {
  const navigate = useNavigate();

  const handleCreateShopClick = () => {
    navigate("/create-shop");
  };

  return (
    <div style={styles.container}>
      <div style={styles.textContainer}>
        <h1 style={styles.heading}>Create Your Own Online Shop</h1>
        <p style={styles.subheading}>
          With just one click, you can start your journey.
        </p>
        <button style={styles.button} onClick={handleCreateShopClick}>
          <FaShopify style={styles.buttonIcon} /> Create a Shop
        </button>
        <p style={styles.footerText}>
          Turn your dream into reality and thrive online.
        </p>
      </div>
    </div>
  );
};

const styles = {
  container: {
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
    justifyContent: "center",
    height: "100vh",
    background: "linear-gradient(to right, #6a11cb 0%, #2575fc 100%)",
    color: "white",
    textAlign: "center",
    padding: "20px",
    boxSizing: "border-box",
  },
  textContainer: {
    maxWidth: "600px",
  },
  heading: {
    fontSize: "3rem",
    marginBottom: "20px",
    fontWeight: "bold",
    animation: "fadeIn 2s ease-in-out",
  },
  subheading: {
    fontSize: "1.5rem",
    marginBottom: "30px",
    fontWeight: "300",
    opacity: "0.9",
  },
  button: {
    fontSize: "1.25rem",
    padding: "15px 30px",
    backgroundColor: "#28a745",
    color: "white",
    border: "none",
    borderRadius: "5px",
    cursor: "pointer",
    display: "flex",
    alignItems: "center",
    justifyContent: "center",
    transition: "background-color 0.3s ease",
    boxShadow: "0 4px 8px rgba(0, 0, 0, 0.2)",
  },
  buttonIcon: {
    marginRight: "10px",
  },
  footerText: {
    marginTop: "20px",
    fontSize: "1rem",
    opacity: "0.8",
  },
  "@keyframes fadeIn": {
    "0%": { opacity: 0 },
    "100%": { opacity: 1 },
  },
};

export default Billboard;
