import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";

const Dashboard = ({ shopName }) => {
  const [products, setProducts] = useState([]);
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [newProduct, setNewProduct] = useState({
    name: "",
    description: "",
    price: "",
    shopId: localStorage.getItem("shopId"),
  });

  useEffect(() => {
    GetProducts();
  }, []);
  const GetProducts = async () => {
    const shopId = localStorage.getItem("shopId");
    const token = localStorage.getItem("token");
    const tokenHeader = "Bearer " + token;
    try {
      const response = await fetch(
        `https://localhost:7118/api/Shop/get-products?shopId=${shopId}`,
        {
          method: "Get",
          headers: {
            "Content-type": "application/json",
            Authorization: tokenHeader,
          },
        }
      );
      if (!response.ok) {
        throw new Error("Response was not okay");
      }
      const data = await response.json();
      console.log(data);
      navigate("/dashboard");
    } catch (error) {
      console.error("There was a problem with get products", error);
    }
  };
  const navigate = useNavigate();

  const openModal = () => setIsModalOpen(true);
  const closeModal = () => setIsModalOpen(false);

  const handleChange = (e) => {
    setNewProduct({ ...newProduct, [e.target.name]: e.target.value });
  };

  const handleAddProduct = async () => {
    setProducts([...products, newProduct]);
    setNewProduct({ name: "", description: "", price: "" });
    const body = JSON.stringify(newProduct);
    const userId = localStorage.getItem("userId");
    const token = localStorage.getItem("token");
    const tokenHeader = "Bearer " + token;
    try {
      const response = await fetch(
        "https://localhost:7118/api/Shop/add-product",
        {
          method: "Post",
          headers: {
            "Content-type": "application/json",
            Authorization: tokenHeader,
          },
          body: body,
        }
      );
      if (!response.ok) {
        throw new Error("Response was not okay");
      }
      const data = await response.json();
      console.log(data);
      navigate("/dashboard");
    } catch (error) {
      console.error("There was a problem with adding product", error);
    }
    closeModal();
  };

  return (
    <div style={styles.container}>
      <h1 style={styles.shopName}>{shopName}</h1>

      <button style={styles.addButton} onClick={openModal}>
        Add Product
      </button>

      <table style={styles.table}>
        <thead>
          <tr>
            <th style={styles.tableHeader}>Product Name</th>
            <th style={styles.tableHeader}>Description</th>
            <th style={styles.tableHeader}>Price</th>
          </tr>
        </thead>
        <tbody>
          {products.map((product, index) => (
            <tr key={index}>
              <td style={styles.tableCell}>{product.name}</td>
              <td style={styles.tableCell}>{product.description}</td>
              <td style={styles.tableCell}>${product.price}</td>
            </tr>
          ))}
        </tbody>
      </table>

      {isModalOpen && (
        <div style={styles.modalOverlay}>
          <div style={styles.modal}>
            <h2 style={styles.modalTitle}>Add Product</h2>
            <input
              type="text"
              name="name"
              placeholder="Product Name"
              value={newProduct.name}
              onChange={handleChange}
              style={styles.input}
            />
            <input
              type="text"
              name="description"
              placeholder="Product Description"
              value={newProduct.description}
              onChange={handleChange}
              style={styles.input}
            />
            <input
              type="number"
              name="price"
              placeholder="Price"
              value={newProduct.price}
              onChange={handleChange}
              style={styles.input}
            />
            <button style={styles.modalAddButton} onClick={handleAddProduct}>
              Add
            </button>
            <button style={styles.modalCloseButton} onClick={closeModal}>
              Close
            </button>
          </div>
        </div>
      )}
    </div>
  );
};

const styles = {
  container: {
    padding: "20px",
    fontFamily: "Arial, sans-serif",
  },
  shopName: {
    fontSize: "2rem",
    textAlign: "center",
    marginBottom: "30px",
  },
  table: {
    width: "100%",
    borderCollapse: "collapse",
    marginBottom: "30px",
  },
  tableHeader: {
    borderBottom: "2px solid #ccc",
    padding: "10px",
    textAlign: "left",
    fontSize: "1.1rem",
    backgroundColor: "#f4f4f4",
  },
  tableCell: {
    padding: "10px",
    borderBottom: "1px solid #ddd",
  },
  addButton: {
    padding: "10px 20px",
    backgroundColor: "blue",
    color: "white",
    border: "none",
    borderRadius: "5px",
    cursor: "pointer",
    fontSize: "16px",
    marginBottom: "20px",
  },
  modalOverlay: {
    position: "fixed",
    top: 0,
    left: 0,
    right: 0,
    bottom: 0,
    backgroundColor: "rgba(0,0,0,0.5)",
    display: "flex",
    alignItems: "center",
    justifyContent: "center",
  },
  modal: {
    backgroundColor: "white",
    padding: "20px",
    borderRadius: "8px",
    width: "300px",
    boxShadow: "0 2px 10px rgba(0, 0, 0, 0.2)",
  },
  modalTitle: {
    fontSize: "1.5rem",
    marginBottom: "20px",
    textAlign: "center",
  },
  input: {
    width: "100%",
    padding: "10px",
    marginBottom: "10px",
    borderRadius: "5px",
    border: "1px solid #ddd",
    fontSize: "16px",
  },
  modalAddButton: {
    width: "100%",
    padding: "10px",
    backgroundColor: "green",
    color: "white",
    border: "none",
    borderRadius: "5px",
    cursor: "pointer",
    fontSize: "16px",
  },
  modalCloseButton: {
    width: "100%",
    padding: "10px",
    backgroundColor: "red",
    color: "white",
    border: "none",
    borderRadius: "5px",
    cursor: "pointer",
    fontSize: "16px",
    marginTop: "10px",
  },
};

export default Dashboard;
