import React, { useState } from "react";
import { Link } from "react-router-dom";

const Navbar = ({ state }) => {
  console.log(state);
  const [dropdownOpen, setDropdownOpen] = useState(false);

  const toggleDropdown = () => {
    setDropdownOpen(!dropdownOpen);
  };

  return (
    <nav style={styles.navbar}>
      <a href="/home" style={styles.navItem}>
        Home
      </a>
      <div style={styles.rightItems}>
        {state.isLoggedIn ? (
          <div style={styles.dropdownContainer}>
            <button onClick={toggleDropdown} style={styles.userButton}>
              {state.userName} <span style={styles.arrow}>&#9662;</span>
            </button>
            {dropdownOpen && (
              <div style={styles.dropdownMenu}>
                <a href="/account-settings" style={styles.dropdownItem}>
                  Account Settings
                </a>
                <a href="/logout" style={styles.dropdownItem}>
                  Logout
                </a>
              </div>
            )}
          </div>
        ) : (
          <>
            <a href="/register" style={styles.navItem}>
              Register
            </a>
            <a href="/login" style={styles.navItem}>
              Login
            </a>
          </>
        )}
      </div>
    </nav>
  );
};

const styles = {
  navbar: {
    display: "flex",
    justifyContent: "space-between",
    alignItems: "center",
    padding: "10px 40px",
    backgroundColor: "#333",
  },
  rightItems: {
    display: "flex",
    alignItems: "center",
    gap: "20px",
  },
  navItem: {
    color: "white",
    textDecoration: "none",
    fontSize: "18px",
  },
  userButton: {
    backgroundColor: "blue",
    color: "white",
    border: "none",
    fontSize: "18px",
    padding: "10px 20px",
    borderRadius: "5px",
    cursor: "pointer",
    position: "relative",
    display: "flex",
    alignItems: "center",
  },
  arrow: {
    marginLeft: "8px",
    fontSize: "12px",
  },
  dropdownContainer: {
    position: "relative",
  },
  dropdownMenu: {
    position: "absolute",
    top: "40px", // Slightly below the button
    right: "0",
    backgroundColor: "#444",
    borderRadius: "4px",
    boxShadow: "0px 0px 10px rgba(0, 0, 0, 0.1)",
    zIndex: 1000,
  },
  dropdownItem: {
    display: "block",
    padding: "10px 20px",
    color: "white",
    textDecoration: "none",
    fontSize: "16px",
    cursor: "pointer",
  },
};

export default Navbar;
