import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import AddProduct from "./Add-Product";
import { Trash2, Plus, X } from "lucide-react";
import { Alert, AlertDescription, AlertTitle } from "@/components/ui/alert";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import {
  Dialog,
  DialogContent,
  DialogDescription,
  DialogFooter,
  DialogHeader,
  DialogTitle,
} from "@/components/ui/dialog";
import {
  Table,
  TableBody,
  TableCell,
  TableHead,
  TableHeader,
  TableRow,
} from "@/components/ui/table";

const Dashboard = () => {
  const [products, setProducts] = useState([]);
  const [productToDelete, setProductToDelete] = useState(null);
  const [showDeleteModal, setShowDeleteModal] = useState(false);
  const [showAddModal, setShowAddModal] = useState(false);
  const [error, setError] = useState(null);
  const navigate = useNavigate();
  const shopName = localStorage.getItem("shopName");

  useEffect(() => {
    GetProducts();
  }, []);

  const GetProducts = async () => {
    const shopId = localStorage.getItem("shopId");
    const token = localStorage.getItem("token");
    const tokenHeader = "Bearer " + token;
    try {
      const response = await fetch(
        `https://localhost:7118/api/Shop/get-products?shopId=${shopId}`,
        {
          method: "GET",
          headers: {
            "Content-type": "application/json",
            Authorization: tokenHeader,
          },
        }
      );
      if (!response.ok) {
        throw new Error("Failed to fetch products");
      }
      const data = await response.json();
      setProducts(data);
    } catch (error) {
      setError("Failed to fetch products. Please try again later.");
    }
  };

  const handleAddProduct = async (newProduct) => {
    try {
      const token = localStorage.getItem("token");
      const response = await fetch(
        "https://localhost:7118/api/Shop/add-product",
        {
          method: "POST",
          headers: {
            "Content-type": "application/json",
            Authorization: `Bearer ${token}`,
          },
          body: JSON.stringify(newProduct),
        }
      );
      if (!response.ok) {
        throw new Error("Failed to add product");
      }
      const addedProduct = await response.json();
      setProducts([...products, addedProduct]);
      setShowAddModal(false);
    } catch (error) {
      setError("Failed to add product. Please try again.");
    }
  };

  const handleDeleteClick = (product) => {
    setProductToDelete(product);
    setShowDeleteModal(true);
  };

  const DeleteProduct = async () => {
    try {
      const token = localStorage.getItem("token");
      const response = await fetch(
        `https://localhost:7118/api/Shop/delete-product?productId=${productToDelete.id}`,
        {
          method: "POST",
          headers: {
            "Content-type": "application/json",
            Authorization: `Bearer ${token}`,
          },
        }
      );
      if (!response.ok) {
        throw new Error("Failed to delete product");
      }
      setProducts(
        products.filter((product) => product.id !== productToDelete.id)
      );
      setShowDeleteModal(false);
      setProductToDelete(null);
    } catch (error) {
      setError("Failed to delete product. Please try again.");
    }
  };

  return (
    <div className="container mx-auto p-4">
      <h1 className="text-3xl font-bold text-center mb-8">{shopName}</h1>

      {error && (
        <Alert variant="destructive" className="mb-4">
          <AlertTitle>Error</AlertTitle>
          <AlertDescription>{error}</AlertDescription>
        </Alert>
      )}

      <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
        <Card className="col-span-2">
          <CardHeader>
            <CardTitle className="flex justify-between items-center">
              Your Products
              <Button
                onClick={() => setShowAddModal(true)}
                className="bg-green-500 hover:bg-green-600"
              >
                <Plus className="mr-2 h-4 w-4" /> Add Product
              </Button>
            </CardTitle>
          </CardHeader>
          <CardContent>
            <Table>
              <TableHeader>
                <TableRow>
                  <TableHead>Product Name</TableHead>
                  <TableHead>Description</TableHead>
                  <TableHead>Price</TableHead>
                  <TableHead>Actions</TableHead>
                </TableRow>
              </TableHeader>
              <TableBody>
                {products.map((product) => (
                  <TableRow key={product.id}>
                    <TableCell>{product.name}</TableCell>
                    <TableCell>{product.description}</TableCell>
                    <TableCell>${product.price.toFixed(2)}</TableCell>
                    <TableCell>
                      <Button
                        variant="destructive"
                        size="sm"
                        onClick={() => handleDeleteClick(product)}
                      >
                        <Trash2 className="h-4 w-4" />
                      </Button>
                    </TableCell>
                  </TableRow>
                ))}
              </TableBody>
            </Table>
          </CardContent>
        </Card>

        <Card>
          <CardHeader>
            <CardTitle>Dashboard Summary</CardTitle>
          </CardHeader>
          <CardContent>
            <p>Total Products: {products.length}</p>
            {/* Add more summary information here */}
          </CardContent>
        </Card>
      </div>

      <Dialog open={showDeleteModal} onOpenChange={setShowDeleteModal}>
        <DialogContent>
          <DialogHeader>
            <DialogTitle>Confirm Deletion</DialogTitle>
            <DialogDescription>
              Are you sure you want to delete this product? This action cannot
              be undone.
            </DialogDescription>
          </DialogHeader>
          <DialogFooter>
            <Button variant="outline" onClick={() => setShowDeleteModal(false)}>
              Cancel
            </Button>
            <Button variant="destructive" onClick={DeleteProduct}>
              Delete
            </Button>
          </DialogFooter>
        </DialogContent>
      </Dialog>

      <Dialog open={showAddModal} onOpenChange={setShowAddModal}>
        <DialogContent>
          <DialogHeader>
            <DialogTitle>Add New Product</DialogTitle>
          </DialogHeader>
          <AddProduct onAddProduct={handleAddProduct} />
        </DialogContent>
      </Dialog>
    </div>
  );
};

export default Dashboard;
